//frontend\src\pages\ResetPassword.jsx

import { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './ResetPassword.css';

export default function ResetPassword() {
  const [form, setForm] = useState({
    email: '',
    otp: '',
    newPassword: '',
  });
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setMsg('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/reset-password', form);
      setMsg(res.data.message || 'Password reset successful!');
      setTimeout(() => navigate('/login'), 2000); // Redirect to login after 2 seconds
    } catch (err) {
      setMsg(err.response?.data?.message || 'Failed to reset password');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reset-page">
      <div className="reset-container">
        <h1 className="reset-title">Reset Password</h1>
        <p className="reset-desc">
          Enter your email, OTP received, and your new password to reset your account password.
        </p>
        <form onSubmit={handleSubmit} className="reset-form">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="otp"
            placeholder="OTP"
            value={form.otp}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="newPassword"
            placeholder="New Password"
            value={form.newPassword}
            onChange={handleChange}
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Resetting...' : 'Reset Password'}
          </button>
        </form>
        {msg && (
          <p className="reset-msg" style={{ color: msg.includes('successful') ? 'green' : 'red' }}>
            {msg}
          </p>
        )}
        <div className="reset-links">
          <Link to="/login">Back to Login</Link>
          <span> | </span>
          <Link to="/signup">Signup</Link>
        </div>
      </div>
    </div>
  );
}
