// frontend/src/pages/MyAppointments.jsx
import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./MyAppointments.css";

export default function MyAppointments() {
  const { user: patient, token } = useAuth(); // logged-in patient
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchAppointments = async () => {
    if (!patient) return;
    try {
      setLoading(true);
      const res = await axios.get(
        `http://localhost:5000/api/appointments/patient/${patient.userId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
      setError("Failed to load your appointments.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, [patient]);

  if (loading) return <p>Loading your appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>You have no appointments.</p>;

  return (
    <div className="my-appointments-page">
      <h1>My Appointments</h1>
      <ul className="appointment-list">
        {appointments.map((appt) => (
          <li key={appt._id} className="appointment-card">
            <p><strong>Doctor:</strong> {appt.doctorName}</p>
            <p><strong>Email:</strong> {appt.doctorEmail}</p>
            <p><strong>Date:</strong> {appt.date}</p>
            <p><strong>Time:</strong> {appt.time}</p>
            <p><strong>Status:</strong> {appt.status}</p>
            {appt.meetLink && (
              <p>
                <strong>Meeting Link:</strong>{" "}
                <a href={appt.meetLink} target="_blank" rel="noreferrer">
                  Join
                </a>
              </p>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
