import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import './DoctorCard.css';

export default function DoctorCard({ doctor }) {
  const [imgSrc, setImgSrc] = useState('');

  useEffect(() => {
    if (!doctor?.image) {
      setImgSrc('');
      return;
    }

    const url = doctor.image.startsWith('http') 
      ? doctor.image 
      : doctor.image.startsWith('/uploads') 
        ? `http://localhost:5000${doctor.image}` 
        : `http://localhost:5000/uploads/${doctor.image}`;

    const img = new Image();
    img.src = url;
    img.onload = () => setImgSrc(url);
    img.onerror = () => setImgSrc(''); // leave blank if fails
  }, [doctor]);

  return (
    <div className="doctor-card">
      {imgSrc && <img src={imgSrc} alt={doctor.name} className="doctor-card-image" />}
      <div className="doctor-card-info">
        <h2>{doctor.name}</h2>
        {doctor.specialization && <p>Specialization: {doctor.specialization}</p>}
        {doctor.experience !== undefined && <p>Experience: {doctor.experience} yrs</p>}
        {doctor.rating !== undefined && <p>Rating: {doctor.rating}</p>}
        <Link to={`/doctors/${doctor.userId}`}>View Details</Link>
      </div>
    </div>
  );
}
