// frontend/src/pages/BookAppointment.jsx
import { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./BookAppointment.css";

export default function BookAppointment() {
  const { id } = useParams(); // doctor userId from URL
  const { user } = useAuth(); // logged-in patient
  const navigate = useNavigate();

  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchDoctor = async () => {
      if (!id) {
        setError("No doctor selected.");
        setLoading(false);
        return;
      }
      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/${id}`);
        setDoctor(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load doctor details.");
      } finally {
        setLoading(false);
      }
    };

    fetchDoctor();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!date || !time) {
      alert("Please select both date and time.");
      return;
    }

    if (!user) {
      alert("You must be logged in to book an appointment.");
      return;
    }

    const appointmentData = {
      patientId: user.userId,
      patientName: user.name,
      patientEmail: user.email,
      doctorId: doctor.userId,
      doctorName: doctor.name,
      doctorEmail: doctor.email,
      date,
      time,
    };

    try {
      setSubmitting(true);
      const res = await axios.post(
        "http://localhost:5000/api/appointments",
        appointmentData
      );
      alert("Appointment request sent successfully!");
      navigate("/my-appointments");
    } catch (err) {
      console.error(err);
      alert(
        err?.response?.data?.message ||
          "Failed to book appointment. Please try again."
      );
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <p>Loading doctor details...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!doctor) return <p>Doctor not found.</p>;

  return (
    <div className="book-appointment-page">
      <Link to={`/doctors/${doctor.userId}`}>‚Üê Back to Doctor Details</Link>
      <h1>Book Appointment with {doctor.name}</h1>
      <div className="doctor-info">
        {doctor.image && (
          <img
            src={`http://localhost:5000${doctor.image}`}
            alt={doctor.name}
            className="doctor-photo"
          />
        )}
        <p><strong>Specialization:</strong> {doctor.specialization || "N/A"}</p>
        <p><strong>Location:</strong> {doctor.location || "N/A"}</p>
      </div>

      <form className="appointment-form" onSubmit={handleSubmit}>
        <h2>Your Information</h2>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>

        <h2>Appointment Details</h2>
        <label>
          Date:
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </label>
        <label>
          Time:
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </label>

        <button type="submit" disabled={submitting}>
          {submitting ? "Submitting..." : "Book Appointment"}
        </button>
      </form>
    </div>
  );
}
