// frontend/src/pages/MyAppointments.jsx
import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./Appointments.css";

export default function MyAppointments() {
  const { user, token } = useAuth(); // logged-in patient
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchAppointments = async () => {
    if (!user) return;
    try {
      setLoading(true);
      const res = await axios.get(
        `http://localhost:5000/api/appointments/patient/${user.userId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
      setError(
        err?.response?.data?.message || "Failed to load appointments"
      );
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, [user]);

  if (loading) return <p>Loading appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>You have no appointments yet.</p>;

  return (
    <div className="appointments-page">
      <h1>My Appointments</h1>
      {appointments.map((appt) => (
        <div key={appt._id} className="appointment-card">
          <p><strong>Doctor:</strong> {appt.doctorName}</p>
          <p><strong>Email:</strong> {appt.doctorEmail}</p>
          <p><strong>Date:</strong> {appt.date}</p>
          <p><strong>Time:</strong> {appt.time}</p>
          <p><strong>Status:</strong> {appt.status}</p>
        </div>
      ))}
    </div>
  );
}
