import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function BookAppointment() {
  const { id } = useParams(); // doctorId
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        const res = await axios.get("http://localhost:5000/data/dummyDoctorData.json");
        const doctorData = res.data.find((d) => d.userId === id);
        if (!doctorData) throw new Error("Doctor not found");
        setDoctor(doctorData);
      } catch (err) {
        console.error(err);
        setError("Failed to load doctor details.");
      } finally {
        setLoading(false);
      }
    };
    fetchDoctor();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!date || !time) return setError("Please select date and time.");

    try {
      const res = await axios.post(
        "http://localhost:5000/api/appointments",
        {
          patientId: user.userId,
          doctorId: doctor.userId,
          date,
          time
        },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      setSuccess("Appointment requested successfully!");
      setTimeout(() => navigate("/my-appointments"), 1500);
    } catch (err) {
      console.error(err);
      setError(err?.response?.data?.message || "Failed to book appointment.");
    }
  };

  if (loading) return <p>Loading doctor...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h1>Book Appointment with {doctor?.name}</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Date: </label>
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        </div>
        <div>
          <label>Time: </label>
          <input type="time" value={time} onChange={(e) => setTime(e.target.value)} required />
        </div>
        <button type="submit">Book Appointment</button>
      </form>
      {success && <p style={{ color: "green" }}>{success}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
