// frontend/src/pages/DoctorAppointments.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./DoctorAppointments.css";

const DoctorAppointments = () => {
  const { user } = useAuth(); // logged-in doctor
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // ------------------ Fetch doctor's appointments ------------------
  useEffect(() => {
    const fetchAppointments = async () => {
      if (!user?.userId) return;
      try {
        const res = await axios.get(
          `http://localhost:5000/api/appointments/doctor/${user.userId}`
        );
        setAppointments(res.data || []);
      } catch (err) {
        console.error("Error fetching appointments:", err);
        setError("Failed to fetch appointments.");
      } finally {
        setLoading(false);
      }
    };
    fetchAppointments();
  }, [user?.userId]);

  // ------------------ Approve / Reject Appointment ------------------
  const handleAction = async (appointmentId, action, meetLink = "") => {
    try {
      const res = await axios.put(
        `http://localhost:5000/api/appointments/${appointmentId}`,
        { status: action, meetLink }
      );

      setAppointments((prev) =>
        prev.map((a) =>
          a.id === appointmentId
            ? { ...a, status: action, meetLink: res.data.meetLink }
            : a
        )
      );
    } catch (err) {
      console.error("Error updating appointment:", err);
      alert("Failed to update appointment");
    }
  };

  // ------------------ Upload Prescription ------------------
  const handlePrescriptionUpload = async (appointmentId, file) => {
    if (!file) return;
    const formData = new FormData();
    formData.append("prescription", file);

    try {
      const res = await axios.post(
        `http://localhost:5000/api/appointments/${appointmentId}/prescription`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      setAppointments((prev) =>
        prev.map((a) =>
          a.id === appointmentId
            ? { ...a, prescription: res.data.appointment.prescription }
            : a
        )
      );
      alert("Prescription uploaded successfully!");
    } catch (err) {
      console.error("Error uploading prescription:", err);
      alert("Failed to upload prescription");
    }
  };

  // ------------------ Render ------------------
  if (loading) return <p className="loading-text">Loading appointments...</p>;
  if (error) return <p className="error-text">{error}</p>;

  return (
    <div className="doctor-appointments-container">
      <h2>Appointment Requests</h2>
      {appointments.length === 0 && <p>No appointments found.</p>}

      {appointments.map((app) => (
        <div key={app.id} className="appointment-card">
          <p>
            <strong>Patient:</strong> {app.patientName} ({app.patientEmail})
          </p>
          <p>
            <strong>Date:</strong> {app.date} <strong>Time:</strong> {app.time}
          </p>
          <p className={`appointment-status appointment-status-${app.status}`}>
            <strong>Status:</strong> {app.status}
          </p>

          {/* Pending Appointments → Approve/Reject */}
          {app.status === "pending" && (
            <div className="action-box">
              <input
                type="text"
                placeholder="Enter meeting link"
                value={app.meetLink || ""}
                onChange={(e) =>
                  setAppointments((prev) =>
                    prev.map((a) =>
                      a.id === app.id ? { ...a, meetLink: e.target.value } : a
                    )
                  )
                }
                className="meeting-input"
              />
              <button
                className="btn-approve"
                onClick={() => handleAction(app.id, "approved", app.meetLink)}
              >
                Approve
              </button>
              <button
                className="btn-reject"
                onClick={() => handleAction(app.id, "rejected")}
              >
                Reject
              </button>
            </div>
          )}

          {/* Approved Appointments → Show link + prescription upload */}
          {app.status === "approved" && (
            <>
              <p>
                <strong>Meeting Link:</strong>{" "}
                {app.meetLink ? (
                  <a
                    href={app.meetLink}
                    target="_blank"
                    rel="noreferrer"
                    className="meeting-link"
                  >
                    {app.meetLink}
                  </a>
                ) : (
                  "Not provided"
                )}
              </p>

              <div className="prescription-box">
                <label>
                  <strong>Upload Prescription:</strong>
                </label>
                <input
                  type="file"
                  accept=".pdf,.jpg,.jpeg,.png"
                  onChange={(e) =>
                    handlePrescriptionUpload(app.id, e.target.files[0])
                  }
                />
                {app.prescription && (
                  <p>
                    <a
                      href={`http://localhost:5000${app.prescription}`}
                      target="_blank"
                      rel="noreferrer"
                      className="prescription-link"
                    >
                      View Uploaded Prescription
                    </a>
                  </p>
                )}
              </div>
            </>
          )}

          {/* Rejected Appointments */}
          {app.status === "rejected" && (
            <p className="appointment-status-rejected">Appointment Rejected</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default DoctorAppointments;
