import { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);

  // Load user from localStorage on mount
  useEffect(() => {
    const saved = localStorage.getItem('user');
    if (saved) setUser(JSON.parse(saved));
  }, []);

  // Set axios default Authorization header
  useEffect(() => {
    if (user?.token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${user.token}`;
    } else {
      delete axios.defaults.headers.common['Authorization'];
    }
  }, [user]);

  // ---------------- LOGIN ----------------
  const login = async (email, password) => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', { email, password });
      
      // Backend should return { user, token }
      const loggedUser = res.data.user;
      const token = res.data.token; 
      loggedUser.token = token; // ✅ attach token

      setUser(loggedUser);
      localStorage.setItem('user', JSON.stringify(loggedUser));
      return loggedUser;
    } catch (err) {
      throw err.response?.data || { message: 'Login failed' };
    } finally {
      setLoading(false);
    }
  };

  // ---------------- LOGOUT ----------------
  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
    delete axios.defaults.headers.common['Authorization'];
  };

  // ---------------- SIGNUP ----------------
  const signup = async (name, email, password, role = 'patient') => {
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/auth/signup', { name, email, password, role });
      return res.data; // { message, user }
    } catch (err) {
      throw err.response?.data || { message: 'Signup failed' };
    } finally {
      setLoading(false);
    }
  };

  // ---------------- UPDATE USER ----------------
  const updateUser = (updatedUser) => {
    setUser(updatedUser);
    localStorage.setItem('user', JSON.stringify(updatedUser));
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, logout, signup, updateUser }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
