import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

const DoctorAppointments = () => {
  const { user } = useAuth(); // logged-in doctor
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await axios.get(
          `http://localhost:5000/api/appointments/doctor/${user.userId}`
        );
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch appointments");
      } finally {
        setLoading(false);
      }
    };
    fetchAppointments();
  }, [user.userId]);

  const handleAction = async (appointmentId, action, meetLink = "") => {
    try {
      await axios.put(`http://localhost:5000/api/appointments/${appointmentId}`, {
        status: action,
        meetLink,
      });
      setAppointments(prev =>
        prev.map(a =>
          a.id === appointmentId ? { ...a, status: action, meetLink } : a
        )
      );
    } catch (err) {
      console.error(err);
      alert("Failed to update appointment");
    }
  };

  if (loading) return <p>Loading appointments...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div style={{ maxWidth: "800px", margin: "auto" }}>
      <h2>Appointment Requests</h2>
      {appointments.length === 0 && <p>No appointments found.</p>}
      {appointments.map(app => (
        <div
          key={app.id}
          style={{ border: "1px solid #ccc", padding: "10px", margin: "10px 0" }}
        >
          <p><strong>Patient:</strong> {app.patientName} ({app.patientEmail})</p>
          <p><strong>Date:</strong> {app.date} <strong>Time:</strong> {app.time}</p>
          <p><strong>Status:</strong> {app.status}</p>

          {app.status === "pending" && (
            <div>
              <input
                type="text"
                placeholder="Enter meeting link"
                value={app.meetLink || ""}
                onChange={e =>
                  setAppointments(prev =>
                    prev.map(a =>
                      a.id === app.id ? { ...a, meetLink: e.target.value } : a
                    )
                  )
                }
                style={{ width: "60%", marginRight: "10px" }}
              />
              <button
                onClick={() => handleAction(app.id, "approved", app.meetLink)}
                style={{ marginRight: "5px" }}
              >
                Approve
              </button>
              <button onClick={() => handleAction(app.id, "rejected")}>Reject</button>
            </div>
          )}

          {app.status === "approved" && (
            <p>
              <strong>Meeting Link:</strong>{" "}
              {app.meetLink ? (
                <a href={app.meetLink} target="_blank" rel="noreferrer">
                  {app.meetLink}
                </a>
              ) : (
                "Not provided"
              )}
            </p>
          )}
        </div>
      ))}
    </div>
  );
};

export default DoctorAppointments;
