// frontend/src/pages/DoctorAppointments.jsx
import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./Appointments.css";

export default function DoctorAppointments() {
  const { user, token } = useAuth(); // logged-in doctor
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchAppointments = async () => {
    if (!user) return;
    try {
      setLoading(true);
      const res = await axios.get(
        `http://localhost:5000/api/appointments/doctor/${user.userId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
      setError(
        err?.response?.data?.message || "Failed to load appointments"
      );
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, [user]);

  const handleAction = async (appointmentId, action) => {
    try {
      await axios.put(
        `http://localhost:5000/api/appointments/${appointmentId}`,
        { status: action },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchAppointments(); // refresh list
    } catch (err) {
      console.error(err);
      alert(
        err?.response?.data?.message || `Failed to ${action} appointment`
      );
    }
  };

  if (loading) return <p>Loading appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>No appointment requests.</p>;

  return (
    <div className="appointments-page">
      <h1>Appointment Requests</h1>
      {appointments.map((appt) => (
        <div key={appt._id} className="appointment-card">
          <p><strong>Patient:</strong> {appt.patientName}</p>
          <p><strong>Email:</strong> {appt.patientEmail}</p>
          <p><strong>Date:</strong> {appt.date}</p>
          <p><strong>Time:</strong> {appt.time}</p>
          <p><strong>Status:</strong> {appt.status}</p>

          {appt.status === "pending" && (
            <div className="actions">
              <button onClick={() => handleAction(appt._id, "approved")}>
                Approve
              </button>
              <button onClick={() => handleAction(appt._id, "rejected")}>
                Reject
              </button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
