// frontend/src/components/PharmacistCard.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './PharmacistCard.css';

export default function PharmacistCard({ pharmacist }) {
  const defaultImage = 'http://localhost:5000/uploads/default-pharmacist.png';

  // Use pharmacist.image if available; otherwise fallback
  const [imgSrc, setImgSrc] = useState(
    pharmacist?.image
      ? `http://localhost:5000/uploads/${encodeURIComponent(pharmacist.image)}`
      : defaultImage
  );

  useEffect(() => {
    if (!pharmacist?.image) return;

    // Build full URL
    const url = pharmacist.image.startsWith('http')
      ? pharmacist.image
      : `http://localhost:5000/uploads/${encodeURIComponent(pharmacist.image)}`;

    // Preload image
    const img = new Image();
    img.src = url;
    img.onload = () => setImgSrc(url);
    img.onerror = () => setImgSrc(defaultImage);
  }, [pharmacist]);

  return (
    <div className="pharmacist-card">
      <img
        src={imgSrc}
        alt={pharmacist.name || 'Pharmacist'}
        className="pharmacist-image"
        onError={(e) => { e.target.src = defaultImage; }}
      />
      <h3>{pharmacist.name || 'N/A'}</h3>
      <p><strong>Pharmacy:</strong> {pharmacist.pharmacyName || 'N/A'}</p>
      <p><strong>Location:</strong> {pharmacist.location || 'N/A'}</p>
      <Link to={`/pharmacists/${pharmacist.userId}`} className="view-profile-link">
        View Profile
      </Link>
    </div>
  );
}
