import { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export default function BookAppointment({ currentUser }) {
  const { doctorId } = useParams();
  const [doctor, setDoctor] = useState(null);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/${doctorId}`);
        setDoctor(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchDoctor();
  }, [doctorId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/appointments/request', {
        doctorId,
        date,
        time,
      }, { headers: { Authorization: `Bearer ${currentUser.token}` } });

      setMessage(res.data.message);
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.message || 'Failed to send request');
    }
  };

  if (!doctor) return <p>Loading doctor...</p>;
  if (doctor.userId === currentUser.userId) return <p>You cannot book an appointment with yourself.</p>;

  return (
    <div>
      <h1>Book Appointment with Dr.{doctor.name}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Date:
          <input type="date" value={date} onChange={e => setDate(e.target.value)} required />
        </label>
        <br />
        <label>
          Time:
          <input type="time" value={time} onChange={e => setTime(e.target.value)} required />
        </label>
        <br />
        <button type="submit">Send Request</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}
