import { Link, useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { useState } from "react";
import "./Navbar.css";

export default function Navbar() {
  const { user, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [query, setQuery] = useState("");
  const [showSearch, setShowSearch] = useState(false); // ‚úÖ toggle state

  // Hide navbar on these routes
  const hiddenPaths = ["/login", "/signup", "/forgot-password", "/reset-password"];
  if (hiddenPaths.some((path) => location.pathname.startsWith(path))) return null;

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    navigate(`/search-medicine?name=${encodeURIComponent(query)}`);
    setQuery("");
    setShowSearch(false); // ‚úÖ close after search
  };

  return (
    <nav className="navbar">
      <span
        className="navbar-brand"
        onClick={() => navigate("/")}
        style={{ cursor: "pointer" }}
      >
        Telemedicine
      </span>

      {/* ‚úÖ Toggle Search Button */}
      <button
        type="button"
        className="search-toggle-btn"
        onClick={() => setShowSearch(!showSearch)}
      >
        üîç Search
      </button>

      {/* ‚úÖ Show input only when toggled */}
      {showSearch && (
        <form onSubmit={handleSearch} className="navbar-search">
          <input
            type="text"
            placeholder="Enter medicine name..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            autoFocus
          />
          <button type="submit">Go</button>
        </form>
      )}

      <div className="navbar-links">
        <Link to="/doctors">Doctors</Link>
        <Link to="/patients">Patients</Link>
        <Link to="/pharmacists">Pharmacists</Link>
        <Link to="/search-medicine">Search Medicine</Link>

        {user ? (
          <>
            <Link to="/profile">{user.name}</Link>
            {user.email === "trysamrat1@gmail.com" && (
              <Link to="/admin">Admin Panel</Link>
            )}
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </>
        ) : null}
      </div>
    </nav>
  );
}
