// frontend/src/components/DoctorCard.jsx

import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import './DoctorCard.css';

export default function DoctorCard({ doctor }) {
  const defaultImage = 'http://localhost:5000/uploads/default-doctor.png';

  // Gender-based default fallback
  const genderDefault =
    doctor?.gender?.toLowerCase() === 'female'
      ? 'http://localhost:5000/uploads/Doctor Female.png'
      : doctor?.gender?.toLowerCase() === 'male'
      ? 'http://localhost:5000/uploads/Doctor Male.png'
      : defaultImage;

  const [imgSrc, setImgSrc] = useState(genderDefault);

  useEffect(() => {
    if (!doctor?.image) return;

    // Build URL (handles both absolute http and relative backend paths)
    const url = doctor.image.startsWith('http')
      ? doctor.image
      : `http://localhost:5000${
          doctor.image.startsWith('/') ? doctor.image : '/uploads/' + doctor.image
        }`;

    // Preload image to avoid flicker
    const img = new Image();
    img.src = url;
    img.onload = () => setImgSrc(url);
    img.onerror = () => setImgSrc(genderDefault);
  }, [doctor, genderDefault]);

  return (
    <div className="doctor-card">
      <img
        src={imgSrc}
        alt={doctor.name || 'Doctor'}
        className="doctor-card-image"
        onError={(e) => (e.target.src = defaultImage)}
      />
      <div className="doctor-card-info">
        <h2>{doctor.name || 'N/A'}</h2>
        {doctor.specialization && <p>Specialization: {doctor.specialization}</p>}
        {doctor.experience !== undefined && (
          <p>Experience: {doctor.experience} yrs</p>
        )}
        {doctor.rating !== undefined && <p>Rating: {doctor.rating}</p>}
        <Link to={`/doctors/${doctor.userId}`} className="doctor-card-link">
          View Details
        </Link>
      </div>
    </div>
  );
}
