import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [successMsg, setSuccessMsg] = useState('');
  const [updating, setUpdating] = useState(false);

  const token = localStorage.getItem('token');

  // Fetch doctor profile
  useEffect(() => {
    const fetchProfile = async () => {
      if (!token) {
        setError('You are not logged in');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get('http://localhost:5000/api/doctors/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDoctor(res.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [token]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setDoctor((prev) => ({ ...prev, [name]: value }));
  };

  // Handle profile update
  const handleUpdate = async () => {
    setUpdating(true);
    setError('');
    setSuccessMsg('');
    try {
      const res = await axios.put(
        'http://localhost:5000/api/doctors/me',
        {
          name: doctor.name,
          specialization: doctor.specialization,
          experience: doctor.experience,
          availability: doctor.availability,
          patientsServed: doctor.patientsServed,
          rating: doctor.rating,
          location: doctor.location,
          contact: doctor.contact,
          image: doctor.image,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setDoctor(res.data.doctor);
      setSuccessMsg('Profile updated successfully!');
    } catch (err) {
      setError(err.response?.data?.message || 'Update failed');
    } finally {
      setUpdating(false);
    }
  };

  if (loading) return <p>Loading profile...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!doctor) return <p>No profile found</p>;

  return (
    <div>
      <h1>My Profile</h1>
      <img
        src={doctor.image}
        alt={doctor.name}
        style={{ width: '150px', borderRadius: '50%' }}
      />

      <div style={{ marginTop: '20px' }}>
        <label>
          Name: <input name="name" value={doctor.name} onChange={handleChange} />
        </label>
        <br />
        <label>
          Specialization:{' '}
          <input
            name="specialization"
            value={doctor.specialization}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Experience: <input name="experience" type="number" value={doctor.experience} onChange={handleChange} />
        </label>
        <br />
        <label>
          Availability (comma separated):{' '}
          <input
            name="availability"
            value={doctor.availability.join(', ')}
            onChange={(e) =>
              setDoctor((prev) => ({
                ...prev,
                availability: e.target.value.split(',').map((d) => d.trim()),
              }))
            }
          />
        </label>
        <br />
        <label>
          Patients Served: <input name="patientsServed" type="number" value={doctor.patientsServed} onChange={handleChange} />
        </label>
        <br />
        <label>
          Rating: <input name="rating" type="number" step="0.1" value={doctor.rating} onChange={handleChange} />
        </label>
        <br />
        <label>
          Location: <input name="location" value={doctor.location} onChange={handleChange} />
        </label>
        <br />
        <label>
          Contact: <input name="contact" value={doctor.contact} onChange={handleChange} />
        </label>
        <br />
        <label>
          Image URL: <input name="image" value={doctor.image} onChange={handleChange} />
        </label>
        <br />
        <button onClick={handleUpdate} disabled={updating}>
          {updating ? 'Updating...' : 'Update Profile'}
        </button>
        {successMsg && <p style={{ color: 'green' }}>{successMsg}</p>}
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </div>
    </div>
  );
}
