// frontend/src/pages/MyAppointments.jsx
import { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function MyAppointments() {
  const { user: patient, token } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!patient) return;

    const fetchAppointments = async () => {
      try {
        setLoading(true);
        const res = await axios.get(
          `http://localhost:5000/api/appointments/patient/${patient.userId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
        setError(err?.response?.data?.message || "Failed to load appointments.");
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, [patient, token]);

  if (!patient) return <p>Please log in as a patient.</p>;
  if (loading) return <p>Loading your appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (appointments.length === 0) return <p>You have no appointments yet.</p>;

  return (
    <div className="appointments-page">
      <h1>My Appointments</h1>
      <table>
        <thead>
          <tr>
            <th>Doctor Name</th>
            <th>Email</th>
            <th>Date</th>
            <th>Time</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map((appt) => (
            <tr key={appt._id}>
              <td>{appt.doctorName}</td>
              <td>{appt.doctorEmail}</td>
              <td>{appt.date}</td>
              <td>{appt.time}</td>
              <td>{appt.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
