// frontend/src/pages/DoctorAppointments.jsx
import { useEffect, useState } from "react";
import { useAuth } from "../contexts/AuthContext";

export default function DoctorAppointments() {
  const { user, token } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Fetch all appointments for this doctor
  const fetchAppointments = async () => {
    if (!token) return;

    setLoading(true);
    setError("");
    try {
      const res = await fetch("http://localhost:5000/api/appointments/doctor", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await res.json();

      if (!res.ok) throw new Error(data.message || "Failed to fetch");

      // Filter appointments by doctorId just in case
      const filtered = data.filter(a => a.doctorId === user.userId);

      setAppointments(filtered);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, [token]);

  const handleAction = async (id, status) => {
    try {
      const res = await fetch(`http://localhost:5000/api/appointments/${id}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Action failed");

      // Refresh the appointment list
      fetchAppointments();
      alert(`Appointment ${status}`);
    } catch (err) {
      alert(err.message);
    }
  };

  if (loading) return <p>Loading appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>No appointment requests yet.</p>;

  return (
    <div style={{ maxWidth: "700px", margin: "auto" }}>
      <h2>Appointment Requests</h2>
      <table>
        <thead>
          <tr>
            <th>Patient</th>
            <th>Date</th>
            <th>Time</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map((a) => (
            <tr key={a._id}>
              <td>{a.patientName}</td>
              <td>{a.date}</td>
              <td>{a.time}</td>
              <td>{a.status}</td>
              <td>
                {a.status === "pending" ? (
                  <>
                    <button onClick={() => handleAction(a._id, "approved")}>
                      Accept
                    </button>
                    <button onClick={() => handleAction(a._id, "rejected")}>
                      Reject
                    </button>
                  </>
                ) : (
                  <span>â€”</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
