import { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Signup.css';

export default function Signup() {
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    role: 'patient',
    age: '',
    medicalHistory: '',
    specialization: '',
    experience: '',
    availability: '',
    patientsServed: '',
    rating: '',
    location: '',
    contact: '',
    pharmacyName: ''
  });
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setMsg('');

    try {
      const res = await axios.post('http://localhost:5000/api/auth/signup', form);
      setMsg(res.data.message);
      setTimeout(() => navigate('/login'), 1500);
    } catch (err) {
      setMsg(err.response?.data?.message || 'Signup failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="signup-page">
      <div className="signup-container">
        <h1 className="signup-title">Signup</h1>
        <form onSubmit={handleSubmit} className="signup-form">
          <input name="name" type="text" placeholder="Name" value={form.name} onChange={handleChange} required />
          <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} required />
          <input name="password" type="password" placeholder="Password" value={form.password} onChange={handleChange} required />

          <div className="role-selection">
            <label>
              <input type="radio" name="role" value="patient" checked={form.role === 'patient'} onChange={handleChange} /> Patient
            </label>
            <label>
              <input type="radio" name="role" value="doctor" checked={form.role === 'doctor'} onChange={handleChange} /> Doctor
            </label>
            <label>
              <input type="radio" name="role" value="pharmacist" checked={form.role === 'pharmacist'} onChange={handleChange} /> Pharmacist
            </label>
          </div>

          {/* Role-specific fields */}
          {form.role === 'patient' && (
            <>
              <input
                name="age"
                type="number"
                placeholder="Age"
                value={form.age}
                onChange={handleChange}
              />
            </>
          )}

          {form.role === 'doctor' && (
            <>
              <input
                name="specialization"
                type="text"
                placeholder="Specialization"
                value={form.specialization}
                onChange={handleChange}
                required
              />
              <input
                name="experience"
                type="number"
                placeholder="Experience (years)"
                value={form.experience}
                onChange={handleChange}
              />
              <input
                name="availability"
                type="text"
                placeholder="Availability (comma separated)"
                value={form.availability}
                onChange={handleChange}
              />
              <input
                name="patientsServed"
                type="number"
                placeholder="Patients Served"
                value={form.patientsServed}
                onChange={handleChange}
              />
              <input
                name="rating"
                type="number"
                step="0.1"
                placeholder="Rating"
                value={form.rating}
                onChange={handleChange}
              />
              <input
                name="location"
                type="text"
                placeholder="Location"
                value={form.location}
                onChange={handleChange}
              />
              <input
                name="contact"
                type="text"
                placeholder="Contact Number"
                value={form.contact}
                onChange={handleChange}
              />
              <p className="doctor-note">
                Note: As a doctor, your data will be added to our doctors' list.
              </p>
            </>
          )}

          {form.role === 'pharmacist' && (
            <>
              <input
                name="pharmacyName"
                type="text"
                placeholder="Pharmacy Name"
                value={form.pharmacyName}
                onChange={handleChange}
                required
              />
              <input
                name="location"
                type="text"
                placeholder="Pharmacy Location"
                value={form.location}
                onChange={handleChange}
              />
            </>
          )}

          <button type="submit" className="signup-btn" disabled={loading}>
            {loading ? 'Signing up...' : 'Signup'}
          </button>
        </form>

        {msg && <p className="signup-msg">{msg}</p>}
      </div>
    </div>
  );
}
