// frontend/src/pages/BookAppointment.jsx
import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function BookAppointment() {
  const { doctorId } = useParams();
  const { currentUser } = useAuth();
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/${doctorId}`);
        setDoctor(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load doctor details");
      } finally {
        setLoading(false);
      }
    };
    fetchDoctor();
  }, [doctorId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!date || !time) {
      setError("Please select both date and time");
      return;
    }

    try {
      const res = await axios.post(
        "http://localhost:5000/api/appointments/book",
        { doctorId, date, time },
        {
          headers: { Authorization: `Bearer ${currentUser.token}` },
        }
      );
      setSuccess("Appointment request sent successfully!");
      setError("");
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || "Failed to send appointment request");
      setSuccess("");
    }
  };

  if (loading) return <p>Loading doctor info...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!doctor) return <p>Doctor not found.</p>;

  return (
    <div>
      <Link to="/doctors">‚Üê Back to Doctors</Link>
      <h1>Book Appointment with Dr. {doctor.name}</h1>
      <p><strong>Specialization:</strong> {doctor.specialization}</p>
      <p><strong>Experience:</strong> {doctor.experience} years</p>
      <p><strong>Availability:</strong> {doctor.availability?.join(", ") || "N/A"}</p>

      <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
        <div>
          <label>Date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Time:</label>
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </div>
        <button type="submit">Send Appointment Request</button>
      </form>

      {success && <p style={{ color: "green" }}>{success}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
