import { useEffect, useState } from 'react';
import axios from 'axios';

export default function DoctorAppointments({ currentUser }) {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    const fetchAppointments = async () => {
      const res = await axios.get('http://localhost:5000/api/appointments', {
        headers: { Authorization: `Bearer ${currentUser.token}` },
      });
      setAppointments(res.data.filter(a => a.doctorId === currentUser.userId));
    };
    fetchAppointments();
  }, [currentUser]);

  const handleResponse = async (id, status) => {
    await axios.put(`http://localhost:5000/api/appointments/respond/${id}`, { status }, {
      headers: { Authorization: `Bearer ${currentUser.token}` },
    });
    setAppointments(prev => prev.map(a => a._id === id ? { ...a, status, meetLink: a.meetLink || `https://meet.google.com/${Math.random().toString(36).slice(2,10)}` } : a));
  };

  if (!appointments.length) return <p>No appointment requests</p>;

  return (
    <div>
      <h1>Appointment Requests</h1>
      <ul>
        {appointments.map(a => (
          <li key={a._id}>
            <p>Patient: {a.patientName} | Date: {a.date} | Time: {a.time} | Status: {a.status}</p>
            {a.status === 'pending' && (
              <>
                <button onClick={() => handleResponse(a._id, 'approved')}>Approve</button>
                <button onClick={() => handleResponse(a._id, 'rejected')}>Reject</button>
              </>
            )}
            {a.status === 'approved' && <p>Meet Link: <a href={a.meetLink}>{a.meetLink}</a></p>}
          </li>
        ))}
      </ul>
    </div>
  );
}
