import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [msg, setMsg] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    specialization: '',
    experience: '',
    availability: '',
    patientsServed: '',
    rating: '',
    location: '',
    contact: '',
    image: ''
  });

  const token = localStorage.getItem('token');

  const fetchProfile = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/doctors/me', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDoctor(res.data);
      setFormData({
        name: res.data.name || '',
        specialization: res.data.specialization || '',
        experience: res.data.experience || '',
        availability: res.data.availability?.join(', ') || '',
        patientsServed: res.data.patientsServed || '',
        rating: res.data.rating || '',
        location: res.data.location || '',
        contact: res.data.contact || '',
        image: res.data.image || ''
      });
    } catch (err) {
      setMsg(err.response?.data?.message || 'Failed to load profile');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.put('http://localhost:5000/api/doctors/me', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setDoctor(res.data.doctor);
      setMsg(res.data.message);
    } catch (err) {
      setMsg(err.response?.data?.error || 'Update failed');
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!doctor) return <p>{msg}</p>;

  return (
    <div>
      <h2>My Profile</h2>
      {msg && <p>{msg}</p>}
      <form onSubmit={handleSubmit}>
        <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" />
        <input name="specialization" value={formData.specialization} onChange={handleChange} placeholder="Specialization" />
        <input name="experience" value={formData.experience} onChange={handleChange} placeholder="Experience" type="number" />
        <input name="availability" value={formData.availability} onChange={handleChange} placeholder="Availability (comma separated)" />
        <input name="patientsServed" value={formData.patientsServed} onChange={handleChange} placeholder="Patients Served" type="number" />
        <input name="rating" value={formData.rating} onChange={handleChange} placeholder="Rating" type="number" step="0.1" />
        <input name="location" value={formData.location} onChange={handleChange} placeholder="Location" />
        <input name="contact" value={formData.contact} onChange={handleChange} placeholder="Contact" />
        <input name="image" value={formData.image} onChange={handleChange} placeholder="Image URL" />
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
}
