// frontend/src/pages/BookAppointment.jsx
import { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./BookAppointment.css";

export default function BookAppointment() {
  const { id } = useParams(); // doctorId
  const { user } = useAuth(); // logged-in patient
  const navigate = useNavigate();

  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/${id}`);
        setDoctor(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load doctor details.");
      } finally {
        setLoading(false);
      }
    };

    fetchDoctor();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!date || !time) {
      setError("Please select both date and time.");
      return;
    }

    if (!user || user.role !== "patient") {
      setError("You must be logged in as a patient to book an appointment.");
      return;
    }

    setSubmitting(true);
    setError("");

    const appointmentData = {
      patientId: user.userId,
      patientName: user.name,
      patientEmail: user.email,
      doctorId: doctor.userId,
      doctorName: doctor.name,
      doctorEmail: doctor.email,
      date,
      time,
    };

    try {
      const res = await axios.post(
        "http://localhost:5000/api/appointments",
        appointmentData
      );

      alert("Appointment request sent successfully!");
      navigate("/my-appointments");
    } catch (err) {
      console.error(err);
      setError(
        err?.response?.data?.message || "Failed to send appointment request."
      );
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <p>Loading doctor details...</p>;
  if (error && !doctor) return <p style={{ color: "red" }}>{error}</p>;
  if (!doctor) return <p>Doctor not found.</p>;
  if (user?.userId === doctor.userId)
    return <p>You cannot book an appointment with yourself.</p>;

  return (
    <div className="book-appointment-page">
      <Link to={`/doctors/${doctor.userId}`}>← Back to Doctor</Link>
      <h1>Book Appointment with {doctor.name}</h1>
      <form onSubmit={handleSubmit} className="appointment-form">
        <div>
          <label>Date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Time:</label>
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </div>

        {error && <p className="error">{error}</p>}

        <button type="submit" disabled={submitting}>
          {submitting ? "Sending..." : "Send Appointment Request"}
        </button>
      </form>
    </div>
  );
}
