// frontend/src/pages/Doctors.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Doctors() {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/doctors', {
          headers: token ? { Authorization: `Bearer ${token}` } : {},
        });
        setDoctors(res.data);
      } catch (err) {
        console.error('Error fetching doctors:', err);
        setError(err.response?.data?.message || 'Failed to load doctors');
      } finally {
        setLoading(false);
      }
    };

    fetchDoctors();
  }, [token]);

  if (loading) return <p>Loading doctors...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!doctors.length) return <p>No doctors found.</p>;

  return (
    <div style={{ maxWidth: '900px', margin: '20px auto' }}>
      <h1>Doctors</h1>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
          gap: '20px',
        }}
      >
        {doctors.map((doctor, index) => {
          const doctorId = doctor.userId || index;
          const imageUrl = doctor.image
            ? doctor.image.startsWith('/uploads')
              ? `http://localhost:5000${doctor.image}`
              : doctor.image
            : '/default-doctor.png';

          return (
            <div
              key={doctorId}
              style={{
                border: '1px solid #ccc',
                borderRadius: '10px',
                padding: '15px',
                textAlign: 'center',
                boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
              }}
            >
              <img
                src={imageUrl}
                alt={doctor.name || 'Doctor'}
                style={{
                  width: '120px',
                  height: '120px',
                  borderRadius: '50%',
                  objectFit: 'cover',
                  marginBottom: '10px',
                }}
              />
              <h3>{doctor.name || 'N/A'}</h3>
              <p><strong>Specialization:</strong> {doctor.specialization || 'N/A'}</p>
              <p><strong>Experience:</strong> {doctor.experience ? `${doctor.experience} years` : 'N/A'}</p>
              <p><strong>Rating:</strong> {doctor.rating || 'N/A'}</p>
              <Link
                to={`/doctors/${doctor.userId}`}
                style={{
                  display: 'inline-block',
                  marginTop: '10px',
                  padding: '5px 10px',
                  backgroundColor: '#007bff',
                  color: '#fff',
                  borderRadius: '5px',
                  textDecoration: 'none',
                }}
              >
                View Details
              </Link>
            </div>
          );
        })}
      </div>
    </div>
  );
}
