import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';
import './DoctorDetail.css';

export default function DoctorDetail() {
  const { id } = useParams();
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/${id}`);
        setDoctor(res.data);
      } catch (err) {
        console.error(err);
        setError('Failed to load doctor details.');
      } finally {
        setLoading(false);
      }
    };

    fetchDoctor();
  }, [id]);

  if (loading) return <p className="loading">Loading doctor details...</p>;
  if (error) return <p className="error">{error}</p>;
  if (!doctor) return <p className="no-data">Doctor not found.</p>;

  return (
    <div className="doctor-detail-page">
      <h1 className="doctor-name">{doctor.name}</h1>
      <div className="doctor-info">
        <p><strong>Specialization:</strong> {doctor.specialization}</p>
        <p><strong>Experience:</strong> {doctor.experience} yrs</p>
        <p><strong>Patients Served:</strong> {doctor.patientsServed}</p>
        <p><strong>Rating:</strong> {doctor.rating}</p>
        <p><strong>Availability:</strong> {doctor.availability.join(', ')}</p>
        <p><strong>Location:</strong> {doctor.location}</p>
        <p><strong>Contact:</strong> {doctor.contact}</p>
      </div>
    </div>
  );
}
