import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useState } from 'react';
import axios from 'axios';
import './Navbar.css';

export default function Navbar() {
  const { user, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  // Pages where Navbar should NOT be shown
  const hiddenPaths = ['/login', '/signup', '/forgot-password', '/reset-password'];
  const isHidden = hiddenPaths.some(path => location.pathname.startsWith(path));
  if (isHidden) return null;

  // Logout and redirect to login
  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  // Search medicine availability
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    try {
      const res = await axios.get(
        `http://localhost:5000/api/pharmacists/search/medicine?name=${query}`
      );
      setResults(res.data);
    } catch (err) {
      console.error('Error searching medicine:', err);
      setResults([]);
    }
  };

  return (
    <nav className="navbar">
      <span className="navbar-brand">Telemedicine</span>

      {/* Medicine Search Box */}
      <form onSubmit={handleSearch} className="navbar-search">
        <input
          type="text"
          placeholder="Search medicine..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      <div className="navbar-links">
        <Link to="/doctors">Doctors</Link>
        <Link to="/patients">Patients</Link>
        <Link to="/pharmacists">Pharmacists</Link>

        {user ? (
          <>
            <Link to="/profile">{user.name}</Link>
            {user.email === 'trysamrat1@gmail.com' && (
              <Link to="/admin">Admin Panel</Link>
            )}
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </>
        ) : null}
      </div>

      {/* Medicine search results dropdown */}
      {results.length > 0 && (
        <div className="search-results">
          <ul>
            {results.map((pharmacist) => (
              <li key={pharmacist.userId}>
                <b>{pharmacist.pharmacyName}</b> ({pharmacist.location})  
                â€“ has {query}
              </li>
            ))}
          </ul>
        </div>
      )}
    </nav>
  );
}
