import { Link, useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { useState } from "react";
import "./Navbar.css";

export default function Navbar() {
  const { user, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [query, setQuery] = useState("");

  // Hide navbar on these routes
  const hiddenPaths = ["/login", "/signup", "/forgot-password", "/reset-password"];
  if (hiddenPaths.some((path) => location.pathname.startsWith(path))) return null;

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  // ✅ Navigate to search page with query
  const handleSearch = (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    navigate(`/search-medicine?name=${encodeURIComponent(query)}`);
    setQuery(""); // clear input
  };

  // ✅ Helper: mark active link
  const isActive = (path) => location.pathname.startsWith(path) ? "active-link" : "";

  return (
    <nav className="navbar">
      <span
        className="navbar-brand"
        onClick={() => navigate("/")}
        style={{ cursor: "pointer" }}
      >
        Telemedicine
      </span>

      {/* Search Bar */}
      <form onSubmit={handleSearch} className="navbar-search">
        <input
          type="text"
          placeholder="Search medicine..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      {/* Navigation Links */}
      <div className="navbar-links">
        <Link to="/doctors" className={isActive("/doctors")}>Doctors</Link>
        <Link to="/patients" className={isActive("/patients")}>Patients</Link>
        <Link to="/pharmacists" className={isActive("/pharmacists")}>Pharmacists</Link>
        <Link to="/search-medicine" className={isActive("/search-medicine")}>
          Search Medicine
        </Link>

        {user ? (
          <>
            <Link to="/profile" className={isActive("/profile")}>
              {user.name}
            </Link>
            {user.email === "trysamrat1@gmail.com" && (
              <Link to="/admin" className={isActive("/admin")}>Admin Panel</Link>
            )}
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </>
        ) : null}
      </div>
    </nav>
  );
}
