import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import PharmacistCard from "../components/PharmacistCard";
import "./MedicineSearch.css";

export default function MedicineSearch() {
  const [queryParams] = useSearchParams();
  const [query, setQuery] = useState(queryParams.get("name") || "");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [suggestions, setSuggestions] = useState([]);

  // Hardcoded quick search medicine list (can later fetch from backend)
  const quickOptions = [
    "Paracetamol",
    "Ibuprofen",
    "Amoxicillin",
    "Cetirizine",
    "Azithromycin",
    "Metformin",
    "Atorvastatin",
    "Amlodipine",
    "Omeprazole",
    "Diclofenac",
  ];

  const handleSearch = async (e) => {
    if (e) e.preventDefault();
    if (!query.trim()) {
      setError("Please enter a medicine name.");
      return;
    }

    setError("");
    setLoading(true);

    try {
      const res = await axios.get(
        `http://localhost:5000/api/pharmacists/search/medicine?name=${encodeURIComponent(
          query
        )}`
      );
      setResults(res.data);
    } catch (err) {
      console.error("Error searching medicine:", err);
      setError("Failed to fetch results.");
    } finally {
      setLoading(false);
    }
  };

  // Run search when query param exists
  useEffect(() => {
    if (query) handleSearch();
    // eslint-disable-next-line
  }, [query]);

  // ‚úÖ Show quick search suggestions while typing
  useEffect(() => {
    if (!query.trim()) {
      setSuggestions([]);
      return;
    }
    const filtered = quickOptions.filter((opt) =>
      opt.toLowerCase().includes(query.toLowerCase())
    );
    setSuggestions(filtered);
  }, [query]);

  // ‚úÖ When user clicks suggestion
  const handleQuickSearch = (medicine) => {
    setQuery(medicine);
    setSuggestions([]); // hide suggestions
    setTimeout(() => handleSearch(), 0);
  };

  return (
    <div className="medicine-search-container">
      <h2>üîç Search Medicine Availability</h2>

      <form onSubmit={handleSearch} className="search-form">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter medicine name (e.g., Paracetamol)"
          className="search-input"
        />
        <button type="submit" className="search-btn">
          Search
        </button>
      </form>

      {/* ‚úÖ Suggestions dropdown */}
      {suggestions.length > 0 && (
        <ul className="suggestions-list">
          {suggestions.map((med) => (
            <li key={med} onClick={() => handleQuickSearch(med)}>
              {med}
            </li>
          ))}
        </ul>
      )}

      {loading && <p>Searching...</p>}
      {error && <p className="error">{error}</p>}

      <div className="pharmacist-results">
        {results.length > 0 ? (
          results.map((pharmacist) => (
            <PharmacistCard
              key={pharmacist.userId}
              pharmacist={pharmacist}
              highlight={query}
            />
          ))
        ) : (
          !loading &&
          query && <p>No pharmacists found stocking "{query}".</p>
        )}
      </div>
    </div>
  );
}
