import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [form, setForm] = useState({});
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const [msg, setMsg] = useState('');
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchProfile = async () => {
      if (!token) {
        setError('You are not logged in');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get('http://localhost:5000/api/users/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(res.data);
        setForm(res.data); // initialize form with current profile
      } catch (err) {
        setError(
          err.response?.data?.error ||
          err.response?.data?.message ||
          'Failed to load profile'
        );
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [token]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setUpdating(true);
    setMsg('');
    setError('');

    try {
      const res = await axios.put('http://localhost:5000/api/users/me', form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProfile(res.data);
      setMsg('Profile updated successfully');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update profile');
    } finally {
      setUpdating(false);
    }
  };

  if (loading) return <p>Loading profile...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!profile) return <p>No profile found</p>;

  return (
    <div>
      <h1>My Profile</h1>
      {profile.image && (
        <img
          src={profile.image}
          alt={profile.name}
          style={{ width: '150px', borderRadius: '50%' }}
        />
      )}

      <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={form.name || ''}
            onChange={handleChange}
          />
        </div>

        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={form.email || ''}
            onChange={handleChange}
          />
        </div>

        {/* Role-specific fields */}
        {profile.role === 'doctor' && (
          <>
            <div>
              <label>Specialization:</label>
              <input
                type="text"
                name="specialization"
                value={form.specialization || ''}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Experience (years):</label>
              <input
                type="number"
                name="experience"
                value={form.experience || ''}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Availability:</label>
              <input
                type="text"
                name="availability"
                value={form.availability?.join(', ') || ''}
                onChange={(e) =>
                  setForm({ ...form, availability: e.target.value.split(',').map(v => v.trim()) })
                }
              />
            </div>
            <div>
              <label>Patients Served:</label>
              <input
                type="number"
                name="patientsServed"
                value={form.patientsServed || ''}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Location:</label>
              <input
                type="text"
                name="location"
                value={form.location || ''}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Contact:</label>
              <input
                type="text"
                name="contact"
                value={form.contact || ''}
                onChange={handleChange}
              />
            </div>
          </>
        )}

        {profile.role === 'patient' && (
          <>
            <div>
              <label>Age:</label>
              <input
                type="number"
                name="age"
                value={form.age || ''}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Medical History:</label>
              <input
                type="text"
                name="medicalHistory"
                value={form.medicalHistory || ''}
                onChange={handleChange}
              />
            </div>
          </>
        )}

        {profile.role === 'pharmacist' && (
          <>
            <div>
              <label>Pharmacy Name:</label>
              <input
                type="text"
                name="pharmacyName"
                value={form.pharmacyName || ''}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>License Number:</label>
              <input
                type="text"
                name="licenseNumber"
                value={form.licenseNumber || ''}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Address:</label>
              <input
                type="text"
                name="address"
                value={form.address || ''}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Available Medicines (comma separated):</label>
              <input
                type="text"
                name="medicines"
                value={form.medicines?.join(', ') || ''}
                onChange={(e) =>
                  setForm({ ...form, medicines: e.target.value.split(',').map(v => v.trim()) })
                }
              />
            </div>
          </>
        )}

        <button type="submit" disabled={updating}>
          {updating ? 'Updating...' : 'Update Profile'}
        </button>
      </form>

      {msg && <p style={{ color: 'green' }}>{msg}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}
