// frontend/src/pages/DoctorAppointments.jsx
import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./DoctorAppointments.css";

export default function DoctorAppointments() {
  const { user: doctor, token } = useAuth(); // logged-in doctor
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchAppointments = async () => {
    if (!doctor) return;
    try {
      setLoading(true);
      const res = await axios.get(
        `http://localhost:5000/api/appointments/doctor/${doctor.userId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
      setError("Failed to load appointments.");
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateStatus = async (appointmentId, status) => {
    try {
      await axios.put(
        `http://localhost:5000/api/appointments/${appointmentId}`,
        { status },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchAppointments(); // refresh list
    } catch (err) {
      console.error(err);
      alert("Failed to update appointment status.");
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, [doctor]);

  if (loading) return <p>Loading appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>No appointments found.</p>;

  return (
    <div className="doctor-appointments-page">
      <h1>Appointments for Dr. {doctor.name}</h1>
      <ul className="appointment-list">
        {appointments.map((appt) => (
          <li key={appt._id} className="appointment-card">
            <p><strong>Patient:</strong> {appt.patientName}</p>
            <p><strong>Email:</strong> {appt.patientEmail}</p>
            <p><strong>Date:</strong> {appt.date}</p>
            <p><strong>Time:</strong> {appt.time}</p>
            <p><strong>Status:</strong> {appt.status}</p>

            {appt.status === "pending" && (
              <div className="action-buttons">
                <button onClick={() => handleUpdateStatus(appt._id, "approved")}>
                  Approve
                </button>
                <button onClick={() => handleUpdateStatus(appt._id, "rejected")}>
                  Reject
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
