// frontend/src/pages/DoctorAppointments.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

export default function DoctorAppointments() {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchAppointments = async () => {
    try {
      const token = localStorage.getItem("token"); // assuming token stored
      const res = await axios.get("/api/appointments/doctor", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const updateStatus = async (id, status) => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.patch(
        `/api/appointments/${id}`,
        { status },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAppointments((prev) =>
        prev.map((a) => (a._id === id ? res.data.appointment : a))
      );
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  if (loading) return <p>Loading appointments...</p>;
  if (!appointments.length) return <p>No appointment requests yet.</p>;

  return (
    <div>
      <h2>Appointment Requests</h2>
      <table className="appointment-table">
        <thead>
          <tr>
            <th>Patient</th>
            <th>Email</th>
            <th>Date</th>
            <th>Time</th>
            <th>Status</th>
            <th>Meeting Link</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map((app) => (
            <tr key={app._id}>
              <td>{app.patientName}</td>
              <td>{app.patientEmail}</td>
              <td>{app.date}</td>
              <td>{app.time}</td>
              <td>{app.status}</td>
              <td>
                {app.status === "approved" && app.meetLink ? (
                  <a href={app.meetLink} target="_blank" rel="noreferrer">
                    Join
                  </a>
                ) : (
                  "-"
                )}
              </td>
              <td>
                {app.status === "pending" && (
                  <>
                    <button onClick={() => updateStatus(app._id, "approved")}>
                      Approve
                    </button>
                    <button onClick={() => updateStatus(app._id, "rejected")}>
                      Reject
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
