// frontend/src/components/DoctorCard.jsx
import { Link } from 'react-router-dom';
import './DoctorCard.css';

export default function DoctorCard({ doctor }) {
  // Determine image based on gender, fallback to default-doctor.png
  let imageUrl;

  if (doctor.gender?.toLowerCase() === 'female') {
    imageUrl = `http://localhost:5000/uploads/Doctor Female.png`;
  } else if (doctor.gender?.toLowerCase() === 'male') {
    imageUrl = `http://localhost:5000/uploads/Doctor Male.png`;
  } else {
    imageUrl = `http://localhost:5000/uploads/default-doctor.png`;
  }

  // If doctor.image exists, use it (optional override)
  if (doctor.image) {
    imageUrl = `http://localhost:5000/uploads/${encodeURIComponent(doctor.image)}`;
  }

  return (
    <div className="doctor-card">
      <img
        src={imageUrl}
        alt={doctor.name || 'Doctor'}
        className="doctor-card-image"
        onError={(e) => {
          e.target.src = 'http://localhost:5000/uploads/default-doctor.png';
        }}
      />
      <div className="doctor-card-info">
        <h2>{doctor.name}</h2>
        {doctor.specialization && <p>Specialization: {doctor.specialization}</p>}
        {doctor.experience !== undefined && <p>Experience: {doctor.experience} yrs</p>}
        {doctor.rating !== undefined && <p>Rating: {doctor.rating}</p>}
        <Link to={`/doctors/${doctor.userId}`} className="doctor-card-link">
          View Details
        </Link>
      </div>
    </div>
  );
}
