import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function MyAppointments() {
  const { user, token } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/appointments/patient", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
        setError(err?.response?.data?.message || "Failed to load appointments");
      } finally {
        setLoading(false);
      }
    };
    if (user?.role === "patient") fetchAppointments();
  }, [user, token]);

  if (loading) return <p>Loading your appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>No appointments found.</p>;

  return (
    <div>
      <h2>My Appointments</h2>
      {appointments.map((appt) => (
        <div key={appt._id} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
          <p>
            <strong>Doctor:</strong> {appt.doctorName} ({appt.doctorEmail})
          </p>
          <p>
            <strong>Date:</strong> {appt.date} <strong>Time:</strong> {appt.time}
          </p>
          <p>
            <strong>Status:</strong> {appt.status}
          </p>
          {appt.meetLink && (
            <p>
              <strong>Meeting Link:</strong>{" "}
              <a href={appt.meetLink} target="_blank" rel="noopener noreferrer">
                {appt.meetLink}
              </a>
            </p>
          )}
        </div>
      ))}
    </div>
  );
}
