import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

export default function Navbar() {
  const { user, logout } = useAuth();
  const location = useLocation();

  // Pages where Navbar should NOT be shown
  const hiddenPaths = ['/login', '/signup', '/forgot-password', '/reset-password'];
  if (hiddenPaths.includes(location.pathname)) return null;

  return (
    <nav className="bg-blue-600 text-white flex items-center p-4">
      {/* Logo / Brand */}
      <Link to="/" className="font-bold text-xl mr-10">
        Telemedicine
      </Link>

      {/* Navigation Links */}
      <div className="flex space-x-6">
        <Link to="/doctors" className="hover:underline">
          Doctors
        </Link>

        {user ? (
          <>
            <Link to="/profile" className="hover:underline">
              {user.name}
            </Link>
            <button
              onClick={logout}
              className="bg-red-500 hover:bg-red-600 px-3 py-1 rounded transition"
            >
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="hover:underline">
              Login
            </Link>
            <Link to="/signup" className="hover:underline">
              Signup
            </Link>
          </>
        )}
      </div>
    </nav>
  );
}
