// frontend/src/pages/Doctors.jsx
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Doctors.css';

export default function Doctors() {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/doctors');
        if (Array.isArray(res.data)) {
          setDoctors(res.data);
        } else {
          setDoctors([]);
          setError('Invalid response from server');
        }
      } catch (err) {
        console.error('Error fetching doctors:', err);
        setError('Failed to load doctors');
      } finally {
        setLoading(false);
      }
    };
    fetchDoctors();
  }, []);

  if (loading) return <p>Loading doctors...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!doctors.length) return <p>No doctors found.</p>;

  return (
    <div className="doctors-page">
      <h1>All Doctors</h1>
      <div className="doctors-grid">
        {doctors.map((doctor) => {
          const photoUrl = doctor.image
            ? `http://localhost:5000${doctor.image}`
            : '/default-doctor.png';

          return (
            <Link
              to={`/doctors/${doctor.userId}`}
              key={doctor.userId}   // ✅ stable key
              className="doctor-card"
            >
              <img
                src={photoUrl}
                alt={doctor.name || 'Doctor'}
                className="doctor-card-photo"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = '/default-doctor.png';
                }}
              />
              <h3>{doctor.name || 'N/A'}</h3>
              <p><strong>Specialization:</strong> {doctor.specialization || 'N/A'}</p>
              <p><strong>Experience:</strong> {doctor.experience || 'N/A'} years</p>
              <p><strong>Location:</strong> {doctor.location || 'N/A'}</p>
            </Link>
          );
        })}
      </div>
    </div>
  );
}
