import { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import defaultProfile from '../assets/defaultProfile.png';

export default function Profile() {
  const { user, updateUser } = useAuth();
  const [form, setForm] = useState({
    name: user.name,
    email: user.email,
    userId: user.userId,
    password: ''
  });
  const [preview, setPreview] = useState(user.profilePhoto ? `http://localhost:5000/uploads/${user.profilePhoto}` : defaultProfile);
  const [msg, setMsg] = useState('');

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleFile = e => {
    const file = e.target.files[0];
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const data = new FormData();
      Object.keys(form).forEach(key => data.append(key, form[key]));
      if (e.target.profilePhoto.files[0]) data.append('profilePhoto', e.target.profilePhoto.files[0]);

      const res = await axios.put(`http://localhost:5000/api/users/${user._id}`, data);
      updateUser(res.data.user);
      setMsg('Profile updated successfully!');
    } catch (err) {
      setMsg(err.response?.data?.message || 'Update failed');
    }
  };

  const handleDeletePhoto = async () => {
    try {
      await axios.delete(`http://localhost:5000/api/users/${user._id}/profile-photo`);
      setPreview(defaultProfile);
      updateUser({ ...user, profilePhoto: undefined });
      setMsg('Profile photo deleted!');
    } catch (err) {
      setMsg('Failed to delete photo');
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h1 className="text-2xl font-bold mb-4">Profile</h1>
      <form onSubmit={handleSubmit} className="space-y-2">
        <div className="flex flex-col items-center">
          <img src={preview} alt="Profile" className="w-24 h-24 rounded-full mb-2" />
          <input type="file" name="profilePhoto" onChange={handleFile} />
          {user.profilePhoto && <button type="button" onClick={handleDeletePhoto} className="text-red-500 mt-1">Delete Photo</button>}
        </div>
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} className="border p-2 w-full" />
        <input name="email" placeholder="Email" value={form.email} onChange={handleChange} className="border p-2 w-full" />
        <input name="userId" placeholder="User ID" value={form.userId} onChange={handleChange} className="border p-2 w-full" />
        <input name="password" placeholder="New Password" type="password" value={form.password} onChange={handleChange} className="border p-2 w-full" />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Update</button>
      </form>
      {msg && <p className="mt-2 text-green-500">{msg}</p>}
    </div>
  );
}
