// frontend/src/pages/MyAppointments.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

const MyAppointments = () => {
  const { user } = useAuth(); // logged-in patient
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/appointments/patient/${user.userId}`);
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch appointments");
      } finally {
        setLoading(false);
      }
    };
    fetchAppointments();
  }, [user.userId]);

  if (loading) return <p>Loading your appointments...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div style={{ maxWidth: "800px", margin: "auto" }}>
      <h2>My Appointments</h2>
      {appointments.length === 0 && <p>No appointments found.</p>}
      {appointments.map(app => (
        <div key={app._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px 0" }}>
          <p><strong>Doctor:</strong> {app.doctorName} ({app.doctorEmail})</p>
          <p><strong>Date:</strong> {app.date} <strong>Time:</strong> {app.time}</p>
          <p><strong>Status:</strong> {app.status}</p>
          {app.status === "approved" && app.meetLink && (
            <p>
              <strong>Meeting Link:</strong>{" "}
              <a href={app.meetLink} target="_blank" rel="noreferrer">
                {app.meetLink}
              </a>
            </p>
          )}
        </div>
      ))}
    </div>
  );
};

export default MyAppointments;
