import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

export default function MyAppointments() {
  const { user } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const res = await axios.get(`/api/appointments/patient/${user.userId}`);
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!appointments.length) return <p>No appointments yet.</p>;

  return (
    <div>
      <h2>My Appointments</h2>
      <ul>
        {appointments.map(appt => (
          <li key={appt._id} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
            <p><strong>Doctor:</strong> {appt.doctorName} ({appt.doctorEmail})</p>
            <p><strong>Date:</strong> {appt.date} | <strong>Time:</strong> {appt.time}</p>
            <p><strong>Status:</strong> {appt.status}</p>
            {appt.status === 'approved' && appt.meetLink && (
              <p><strong>Meeting Link:</strong> <a href={appt.meetLink} target="_blank" rel="noopener noreferrer">{appt.meetLink}</a></p>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
