import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import PharmacistCard from "../components/PharmacistCard";
import "./MedicineSearch.css";

export default function MedicineSearch() {
  const [queryParams] = useSearchParams();
  const [query, setQuery] = useState(queryParams.get("name") || "");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSearch = async (e) => {
    if (e) e.preventDefault();
    if (!query.trim()) {
      setError("Please enter a medicine name.");
      return;
    }
    setError("");
    setLoading(true);

    try {
      const res = await axios.get(
        `http://localhost:5000/api/pharmacists/search/medicine?name=${query}`
      );
      setResults(res.data);
    } catch (err) {
      console.error("Error searching medicine:", err);
      setError("Failed to fetch results.");
    } finally {
      setLoading(false);
    }
  };

  // Run search when query param is present
  useEffect(() => {
    if (query) handleSearch();
    // eslint-disable-next-line
  }, [query]);

  return (
    <div className="medicine-search-container">
      <h2>Search Medicine Availability</h2>
      <form onSubmit={handleSearch} className="search-form">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter medicine name (e.g., Paracetamol)"
          className="search-input"
        />
        <button type="submit" className="search-btn">
          Search
        </button>
      </form>

      {loading && <p>Searching...</p>}
      {error && <p className="error">{error}</p>}

      <div className="pharmacist-results">
        {results.length > 0 ? (
          results.map((pharmacist) => (
            <PharmacistCard key={pharmacist.userId} pharmacist={pharmacist} />
          ))
        ) : (
          !loading && query && <p>No pharmacists found for "{query}"</p>
        )}
      </div>
    </div>
  );
}
