import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export default function DoctorDetail() {
  const { id } = useParams(); // doctor _id from route
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [msg, setMsg] = useState('');

  const fetchDoctor = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/doctors/${id}`);
      setDoctor(res.data);
    } catch (err) {
      setMsg(err.response?.data?.error || 'Failed to load doctor');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDoctor();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (!doctor) return <p>{msg}</p>;

  return (
    <div>
      <h2>{doctor.name}</h2>
      <img src={doctor.image} alt={doctor.name} width="150" />
      <p>Specialization: {doctor.specialization}</p>
      <p>Experience: {doctor.experience} years</p>
      <p>Availability: {doctor.availability.join(', ')}</p>
      <p>Patients Served: {doctor.patientsServed}</p>
      <p>Rating: {doctor.rating}</p>
      <p>Location: {doctor.location}</p>
      <p>Contact: {doctor.contact}</p>
    </div>
  );
}
