// frontend/src/pages/MyAppointments.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

export default function MyAppointments() {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchAppointments = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("/api/appointments/patient", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAppointments(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  if (loading) return <p>Loading your appointments...</p>;
  if (!appointments.length) return <p>No appointments booked yet.</p>;

  return (
    <div>
      <h2>My Appointments</h2>
      <table className="appointment-table">
        <thead>
          <tr>
            <th>Doctor</th>
            <th>Email</th>
            <th>Date</th>
            <th>Time</th>
            <th>Status</th>
            <th>Meeting Link</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map((app) => (
            <tr key={app._id}>
              <td>{app.doctorName}</td>
              <td>{app.doctorEmail}</td>
              <td>{app.date}</td>
              <td>{app.time}</td>
              <td>{app.status}</td>
              <td>
                {app.status === "approved" && app.meetLink ? (
                  <a href={app.meetLink} target="_blank" rel="noreferrer">
                    Join
                  </a>
                ) : (
                  "-"
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
