import { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function BookAppointment() {
  const { doctorId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();

  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSubmit = async e => {
    e.preventDefault();

    if (!date || !time) return setError("Please select date and time.");

    setLoading(true);
    setError("");

    try {
      const doctorRes = await axios.get(`http://localhost:5000/api/doctors/${doctorId}`);
      const doctor = doctorRes.data;

      const res = await axios.post(
        "http://localhost:5000/api/appointments",
        {
          patientId: user.userId,
          patientName: user.name,
          patientEmail: user.email,
          doctorId: doctor.userId,
          doctorName: doctor.name,
          doctorEmail: doctor.email,
          date,
          time
        },
        { headers: { Authorization: `Bearer ${user.token}` } }
      );

      alert("Appointment request sent successfully!");
      navigate("/my-appointments");
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || "Failed to send appointment request.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Book Appointment</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Date: </label>
          <input type="date" value={date} onChange={e => setDate(e.target.value)} required />
        </div>
        <div>
          <label>Time: </label>
          <input type="time" value={time} onChange={e => setTime(e.target.value)} required />
        </div>
        <button type="submit" disabled={loading}>{loading ? "Booking..." : "Book Appointment"}</button>
      </form>
    </div>
  );
}
