import { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import './AdminPanel.css';

export default function AdminPanel() {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [msg, setMsg] = useState('');

  useEffect(() => {
    if (!user || user.email !== 'roys18746@gmail.com') return;

    const fetchUsers = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/admin/users', {
          headers: { Authorization: `Bearer ${user.token}` } // ✅ use user.token
        });
        setUsers(res.data);
      } catch (err) {
        setMsg(err.response?.data?.message || 'Failed to fetch users');
      }
    };
    fetchUsers();
  }, [user]);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/admin/users/${id}`, {
        headers: { Authorization: `Bearer ${user.token}` } // ✅ use user.token
      });
      setUsers(users.filter(u => u._id !== id));
      setMsg('User deleted successfully!');
    } catch (err) {
      setMsg(err.response?.data?.message || 'Delete failed');
    }
  };

  if (!user || user.email !== 'roys18746@gmail.com') {
    return <p style={{ padding: '20px' }}>Access Denied</p>;
  }

  return (
    <div className="admin-page">
      <h1>Admin Control Panel</h1>
      {msg && <p className="msg">{msg}</p>}
      <table className="admin-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>User ID</th>
            <th>Role</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u._id}>
              <td>{u.name}</td>
              <td>{u.email}</td>
              <td>{u.userId}</td>
              <td>{u.role}</td>
              <td>
                <button onClick={() => handleDelete(u._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
