import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import "./MyAppointments.css";

const MyAppointments = () => {
  const { user } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      if (!user?.userId) return;
      try {
        const res = await axios.get(
          `http://localhost:5000/api/appointments/patient/${user.userId}`
        );
        setAppointments(res.data || []);
      } catch (err) {
        console.error("Error fetching appointments:", err);
        setError("Failed to fetch appointments.");
      } finally {
        setLoading(false);
      }
    };
    fetchAppointments();
  }, [user?.userId]);

  const downloadPrescription = (text, doctorName, date) => {
    const element = document.createElement("a");
    const file = new Blob(
      [`Prescription by Dr. ${doctorName} on ${date}\n\n${text}`],
      { type: "text/plain" }
    );
    element.href = URL.createObjectURL(file);
    element.download = `prescription_${doctorName}_${date}.txt`;
    document.body.appendChild(element);
    element.click();
  };

  if (loading) return <p className="loading-text">Loading your appointments...</p>;
  if (error) return <p className="error-text">{error}</p>;

  return (
    <div className="my-appointments-container">
      <h2>My Appointments</h2>
      {appointments.length === 0 && <p>No appointments found.</p>}

      {appointments.map((app) => (
        <div key={app.id} className="appointment-card">
          <p><strong>Doctor:</strong> {app.doctorName} ({app.doctorEmail})</p>
          <p><strong>Date:</strong> {app.date} <strong>Time:</strong> {app.time}</p>
          <p className={`appointment-status appointment-status-${app.status}`}>
            <strong>Status:</strong> {app.status}
          </p>

          {app.status === "approved" && (
            <>
              <p>
                <strong>Meeting Link:</strong>{" "}
                {app.meetLink ? (
                  <a href={app.meetLink} target="_blank" rel="noreferrer" className="meeting-link">
                    {app.meetLink}
                  </a>
                ) : ("Not provided")}
              </p>

              {app.prescription && (
                <div className="prescription-box">
                  <p><strong>Prescription:</strong></p>
                  <pre>{app.prescription}</pre>
                  <button
                    className="btn-download"
                    onClick={() => downloadPrescription(app.prescription, app.doctorName, app.date)}
                  >
                    Download Prescription
                  </button>
                </div>
              )}
            </>
          )}

          {app.status === "rejected" && (
            <p className="appointment-status-rejected">Your appointment was rejected.</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default MyAppointments;
