import { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import defaultProfile from '../assets/defaultProfile.png';
import './Profile.css';

export default function DoctorProfile() {
  const { user, updateUser } = useAuth();

  const [form, setForm] = useState({
    name: user.name || '',
    email: user.email || '',
    userId: user.userId || '',
    password: '',
    specialization: user.specialization || '',
    experience: user.experience || '',
    patientsServed: user.patientsServed || '',
    rating: user.rating || '',
    location: user.location || '',
    contact: user.contact || '',
    availability: user.availability ? user.availability.join(', ') : ''
  });

  const [preview, setPreview] = useState(
    user.profilePhoto || user.image
      ? user.profilePhoto
        ? `http://localhost:5000/uploads/${user.profilePhoto}`
        : user.image
      : defaultProfile
  );
  const [msg, setMsg] = useState('');

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleFile = e => {
    const file = e.target.files[0];
    if (file) setPreview(URL.createObjectURL(file));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const data = new FormData();
      Object.keys(form).forEach(key => data.append(key, form[key]));
      const fileInput = e.target.profilePhoto?.files[0];
      if (fileInput) data.append('profilePhoto', fileInput);

      const res = await axios.put(`http://localhost:5000/api/users/${user._id}`, data);
      updateUser(res.data.user);
      setMsg('Profile updated successfully!');
    } catch (err) {
      setMsg(err.response?.data?.message || 'Update failed');
    }
  };

  const handleDeletePhoto = async () => {
    try {
      await axios.delete(`http://localhost:5000/api/users/${user._id}/profile-photo`);
      setPreview(defaultProfile);
      updateUser({ ...user, profilePhoto: undefined });
      setMsg('Profile photo deleted!');
    } catch (err) {
      setMsg('Failed to delete photo');
    }
  };

  return (
    <div className="profile-page">
      <div className="profile-container">
        <h1 className="profile-title">Doctor Profile</h1>

        <form onSubmit={handleSubmit} className="profile-form">
          {/* Profile Photo */}
          <div className="profile-photo-section">
            <img src={preview} alt="Profile" className="profile-photo" />
            <input type="file" name="profilePhoto" onChange={handleFile} />
            {user.profilePhoto && (
              <button
                type="button"
                className="delete-photo-btn"
                onClick={handleDeletePhoto}
              >
                Delete Photo
              </button>
            )}
          </div>

          {/* Personal info */}
          <input name="name" placeholder="Name" value={form.name} onChange={handleChange} />
          <input name="email" placeholder="Email" value={form.email} onChange={handleChange} />
          <input name="userId" placeholder="User ID" value={form.userId} onChange={handleChange} />
          <input name="password" type="password" placeholder="New Password" value={form.password} onChange={handleChange} />

          {/* Doctor-specific info */}
          <input name="specialization" placeholder="Specialization" value={form.specialization} onChange={handleChange} />
          <input name="experience" type="number" placeholder="Experience (yrs)" value={form.experience} onChange={handleChange} />
          <input name="patientsServed" type="number" placeholder="Patients Served" value={form.patientsServed} onChange={handleChange} />
          <input name="rating" type="number" step="0.1" placeholder="Rating" value={form.rating} onChange={handleChange} />
          <input name="location" placeholder="Location" value={form.location} onChange={handleChange} />
          <input name="contact" placeholder="Contact" value={form.contact} onChange={handleChange} />
          <input name="availability" placeholder="Availability (comma-separated)" value={form.availability} onChange={handleChange} />

          <button type="submit" className="update-btn">Update Profile</button>
        </form>

        {msg && <p className="msg">{msg}</p>}
      </div>
    </div>
  );
}
