import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function DoctorAppointments() {
  const { user, token } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!token) return;

    const fetchAppointments = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/appointments/doctor", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setAppointments(res.data);
      } catch (err) {
        console.error(err);
        alert(err?.response?.data?.message || "Failed to load appointments");
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, [token]);

  const handleStatusChange = async (id, status) => {
    try {
      const res = await axios.patch(
        `http://localhost:5000/api/appointments/${id}`,
        { status },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAppointments((prev) =>
        prev.map((a) => (a._id === id ? res.data.appointment : a))
      );
    } catch (err) {
      console.error(err);
      alert(err?.response?.data?.message || "Failed to update status");
    }
  };

  if (loading) return <p>Loading appointments...</p>;

  return (
    <div>
      <h1>Appointment Requests</h1>
      {appointments.length === 0 ? (
        <p>No requests yet</p>
      ) : (
        <ul>
          {appointments.map((a) => (
            <li key={a._id}>
              {a.patientName} ({a.patientEmail}) - {a.date} {a.time} - Status: {a.status}
              {a.status === "pending" && (
                <>
                  <button onClick={() => handleStatusChange(a._id, "approved")}>Approve</button>
                  <button onClick={() => handleStatusChange(a._id, "rejected")}>Reject</button>
                </>
              )}
              {a.meetLink && <a href={a.meetLink}>Join Meeting</a>}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
