import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export default function DoctorDetail() {
  const { id } = useParams(); // doctor _id from route
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchDoctor = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`http://localhost:5000/api/doctors/${id}`);
      setDoctor(res.data);
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Doctor not found');
      setDoctor(null);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (id) fetchDoctor();
  }, [id]);

  if (loading) return <p>Loading doctor...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div>
      <h1>Doctor Details</h1>
      <img
        src={doctor.image}
        alt={doctor.name}
        style={{ width: '150px', borderRadius: '50%' }}
      />
      <p><strong>Name:</strong> {doctor.name}</p>
      <p><strong>Specialization:</strong> {doctor.specialization}</p>
      <p><strong>Experience:</strong> {doctor.experience} years</p>
      <p><strong>Availability:</strong> {doctor.availability.join(', ')}</p>
      <p><strong>Patients Served:</strong> {doctor.patientsServed}</p>
      <p><strong>Rating:</strong> {doctor.rating}</p>
      <p><strong>Location:</strong> {doctor.location}</p>
      <p><strong>Contact:</strong> {doctor.contact}</p>
    </div>
  );
}
