//frontend\src\pages\DoctorDetail.jsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

export default function DoctorDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchDoctor = async () => {
      if (!id) {
        setError('Invalid doctor ID');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get(`http://localhost:5000/api/doctors/${id}`, {
          headers: token ? { Authorization: `Bearer ${token}` } : {},
        });
        setDoctor(res.data);
      } catch (err) {
        console.error('Error fetching doctor:', err);
        setError(err.response?.data?.message || 'Doctor not found');
      } finally {
        setLoading(false);
      }
    };

    fetchDoctor();
  }, [id, token]);

  if (loading) return <p>Loading doctor details...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!doctor) return <p>No doctor found.</p>;

  return (
    <div style={{ maxWidth: '600px', margin: '20px auto' }}>
      <button
        onClick={() => navigate(-1)}
        style={{ marginBottom: '10px', padding: '5px 10px', cursor: 'pointer' }}
      >
        ‚Üê Back
      </button>
      <h1>{doctor.name || 'N/A'}</h1>
      <p>
        <strong>Specialization:</strong> {doctor.specialization || 'N/A'}
      </p>
      <p>
        <strong>Experience:</strong> {doctor.experience ? `${doctor.experience} years` : 'N/A'}
      </p>
      <p>
        <strong>Availability:</strong>{' '}
        {doctor.availability?.length ? doctor.availability.join(', ') : 'N/A'}
      </p>
      <p>
        <strong>Patients Served:</strong> {doctor.patientsServed || 0}
      </p>
      <p>
        <strong>Rating:</strong> {doctor.rating || 'N/A'}
      </p>
      <p>
        <strong>Location:</strong> {doctor.location || 'N/A'}
      </p>
      <p>
        <strong>Contact:</strong> {doctor.contact || 'N/A'}
      </p>
      <img
        src={
          doctor.image
            ? doctor.image.startsWith('/uploads')
              ? doctor.image
              : doctor.image
            : '/default-doctor.png'
        }
        alt={doctor.name || 'Doctor'}
        style={{ width: '150px', borderRadius: '10px', marginTop: '10px' }}
      />
    </div>
  );
}
