import React, { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";
import axios from "axios";
import './Profile.css';

const ProfilePage = () => {
  const { user, setUser } = useAuth();
  const [formData, setFormData] = useState(null);
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${user._id}`, {
          headers: { Authorization: `Bearer ${user.token}` }
        });
        setFormData(res.data);
        setProfilePhoto(res.data.profilePhoto || null);
      } catch (err) {
        alert("Failed to load user. Please try again later.");
      }
    };

    if (user) fetchUser();
  }, [user]);

  if (!formData) return <p>Loading...</p>;

  // handleChange, handleFileChange, handleSubmit same as before

  return (
    <div className="profile-page">
      <h2>My Profile</h2>
      <form>
        <div>
          <label>Name</label>
          <input name="name" value={formData.name} />
        </div>
        {/* rest of form fields */}
      </form>
    </div>
  );
};

export default ProfilePage;
