// frontend/src/pages/BookAppointment.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

export default function BookAppointment() {
  const { doctorId } = useParams();
  const { user } = useAuth(); // logged-in patient
  const [doctor, setDoctor] = useState(null);
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Fetch doctor details
  useEffect(() => {
    const fetchDoctor = async () => {
      try {
        const res = await axios.get(
          `http://localhost:5000/api/doctors/${doctorId}`
        );
        setDoctor(res.data);
      } catch (err) {
        console.error("Fetch doctor error:", err);
        setMessage("Doctor not found");
      }
    };
    fetchDoctor();
  }, [doctorId]);

  // Handle booking
  const handleBook = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!doctor) return;

    // Prevent booking self
    if (doctor.email === user.email) {
      setMessage("You cannot book an appointment with yourself!");
      return;
    }

    if (!date || !time) {
      setMessage("Please select date and time");
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post(
        "http://localhost:5000/api/appointments",
        {
          patientId: user.userId,
          patientName: user.name,
          patientEmail: user.email,
          doctorId: doctor.userId,
          doctorName: doctor.name,
          doctorEmail: doctor.email,
          date,
          time,
        }
      );
      setMessage("Appointment request sent successfully!");
      setDate("");
      setTime("");
    } catch (err) {
      console.error("Booking error:", err);
      setMessage("Failed to send appointment request");
    }
    setLoading(false);
  };

  if (!doctor) return <div>Loading doctor info...</div>;

  return (
    <div style={{ maxWidth: "500px", margin: "auto" }}>
      <h2>Book Appointment with {doctor.name}</h2>
      <img
        src={doctor.image ? `http://localhost:5000${doctor.image}` : null}
        alt={doctor.name}
        style={{ width: "150px", borderRadius: "50%" }}
      />
      <p>Specialization: {doctor.specialization || "N/A"}</p>
      <p>Location: {doctor.location || "N/A"}</p>
      <p>Experience: {doctor.experience || 0} years</p>
      <p>Availability: {doctor.availability.join(", ") || "N/A"}</p>

      <form onSubmit={handleBook}>
        <div>
          <label>Date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Time:</label>
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? "Sending..." : "Book Appointment"}
        </button>
      </form>

      {message && <p style={{ color: "red" }}>{message}</p>}
    </div>
  );
}
