// frontend/src/pages/MyAppointments.jsx
import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";

export default function MyAppointments() {
  const { currentUser } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/appointments", {
          headers: { Authorization: `Bearer ${currentUser.token}` },
        });
        // Filter only appointments for logged-in patient
        setAppointments(res.data.filter(a => a.patientId === currentUser.userId));
      } catch (err) {
        console.error(err);
        setError("Failed to load appointments");
      } finally {
        setLoading(false);
      }
    };
    fetchAppointments();
  }, [currentUser]);

  if (loading) return <p>Loading your appointments...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!appointments.length) return <p>No appointments found.</p>;

  return (
    <div>
      <h1>My Appointments</h1>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {appointments.map(a => (
          <li key={a._id} style={{ border: "1px solid #ccc", margin: "10px 0", padding: "10px" }}>
            <p>
              <strong>Doctor:</strong> {a.doctorName} | <strong>Email:</strong> {a.doctorEmail}
            </p>
            <p>
              <strong>Date:</strong> {a.date} | <strong>Time:</strong> {a.time} | <strong>Status:</strong> {a.status}
            </p>
            {a.status === "approved" && (
              <p>
                <strong>Meet Link:</strong>{" "}
                <a href={a.meetLink} target="_blank" rel="noreferrer">
                  {a.meetLink}
                </a>
              </p>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
